import React, { useState, useEffect } from 'react';


function App() {
  const [convertedAmount, setConvertedAmount] = useState('');

//   const handleSubmit: any = useEffect(() => {
//       //     event.preventDefault();

// //     const firstName = event.target.firstName.value;
// //     const lastName = event.target.lastName.value;
// //     setMessage(`Hello ${firstName} ${lastName}!`);
//       fetch("http://localhost:8080/currency/?baseCurrency=USD&targetCurrency=EUR&amount=1")
//       .then(response => response.json())
//       .then(data => setConvertedAmount(data))
//   },[])

  const handleSubmit = (event: any) => {
        event.preventDefault();

        const baseCurrency = event.target.baseCurrency.value;
        const targetCurrency = event.target.targetCurrency.value;
        const amount = event.target.amount.value;
        setConvertedAmount("10");    
        event.target.reset();
  };

  const options = ['Monday', 'Tuesday', 'Thursday', 
  'Friday', 'Saturday', 'Sunday']

  return (
    <form onSubmit={handleSubmit}>
    <div>
      <h1>Currency Converter</h1>
      <div>
        <label htmlFor="amount">Amount:</label>
        <input
          type="number"
          id="amount"
        />
        </div>
      <div>
        <label htmlFor="baseCurrency">Base Currency:</label>
        <input
          type="text"
          id="baseCurrency"
        />
      </div>
      <div style={{marginLeft:'40%', marginTop: '60px'}}>
      <h3>Greetings from GeeksforGeeks!</h3>
        <Autocomplete
          options={options}
          style={{ width: 300 }}
          renderInput={(params) =>
            <TextField {...params} label="Combo box" variant="outlined" />}
        />
        </div>
      <div>
        <label htmlFor="targetCurrency">Target Currency:</label>
        <input
          type="text"
          id="targetCurrency"
        />
      </div>
      <button type="submit">Submit</button>
      <p>Converted amount: {convertedAmount}</p>
    </div>
    </form> 
  );
}
export default App;

