import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [amount, setAmount] = useState('');
  const [baseCurrency, setBaseCurrency] = useState('USD');
  const [targetCurrency, setTargetCurrency] = useState('EUR');
  const [convertedAmount, setConvertedAmount] = useState('');

  const handleConvert = async () => {
    try {
      const response = await axios.get(
        `https://api.exchangerate-api.com/v4/latest/${baseCurrency}`
      );

      const rates = response.data.rates;
      const conversionRate = rates[targetCurrency];
      if (conversionRate) {
        const converted = Number(amount) * conversionRate;
        setConvertedAmount(converted.toFixed(2));
      } else {
        throw new Error(`Conversion rate for ${targetCurrency} not found.`);
      }
    } catch (error) {
      console.error('Unable to convert currencies:', error);
    }
  };

  return (
    <div>
      <h1>Currency Converter</h1>
      <div>
        <label htmlFor="amount">Amount:</label>
        <input
          type="number"
          id="amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        </div>
      <div>
        <label htmlFor="baseCurrency">Base Currency:</label>
        <input
          type="text"
          id="baseCurrency"
          value={baseCurrency}
          onChange={(e) => setBaseCurrency(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="targetCurrency">Target Currency:</label>
        <input
          type="text"
          id="targetCurrency"
          value={targetCurrency}
          onChange={(e) => setTargetCurrency(e.target.value)}
        />
      </div>
      <button onClick={handleConvert}>Convert</button>
      {convertedAmount && <p>Converted amount: {convertedAmount}</p>}
    </div>
  );
}

export default App;
